testSimple

      bar = new Bar();
      bar.setAge(33);

      assertEquals("test", echoImplPort.echoString("test"));
      assertEquals("Mary & Paul", echoImplPort.echoString("Mary & Paul"));
      assertEquals(bar.getAge(), echoImplPort.echoBar(bar).getAge());

      cls = new Class2();
      cls.setAge(33);
      assertEquals(cls.getAge(), echoImplPort.echoClass2(cls).getAge());

testArray1

      strArray = new ArrayList();
      strArray.add("Mary");
      strArray.add("Paul");

      returnArray = echoImplPort.echoStringArray(strArray);
      assertEquals(strArray.size(), returnArray.size());
      assertEquals(strArray.get(0), returnArray.get(0));
      assertEquals(strArray.get(1), returnArray.get(1));

testArray2

      bar = new Bar();
      bar.setAge(33);
      Bar bar2 = new Bar();
      bar2.setAge(44);

      barArray = new ArrayList();
      barArray.add(bar);
      barArray.add(bar2);
      resultArray = echoImplPort.echoBarArray(barArray);
      assertEquals(2, resultArray.size());
      assertEquals(bar.getAge(), resultArray.get(0).getAge());
      assertEquals(bar2.getAge(), resultArray.get(1).getAge());

testArray3

      bar = new Bar();
      bar.setAge(33);
      bar2 = new Bar();
      bar2.setAge(44);

      barArray = echoImplPort.echoTwoBar(bar, bar2);
      assertEquals(2, barArray.size());
      assertEquals(bar.getAge(), barArray.get(0).getAge());
      assertEquals(bar2.getAge(), barArray.get(1).getAge());
